			<script>
				const counterPart = document.querySelector('.counter-part');
				const options = {
					rootMargin: '0px',
					threshold: 0.2
				};
				const callback = (entries, observer) => {
					entries.forEach(entry => {
						if (entry.isIntersecting) {
							load();
							observer.unobserve(entry.target);
						}
					});
				};
				const observer = new IntersectionObserver(callback, options);
				observer.observe(counterPart);

				function animate(obj, initVal, lastVal, duration) {
					let startTime = null;
					const step = (currentTime) => {
						if (!startTime) {
							startTime = currentTime;
						}
						const progress = Math.min((currentTime - startTime) / duration, 1);
						obj.textContent = Math.floor(progress * (lastVal - initVal) +
							initVal);
						if (progress < 1) {
							animationFrame(step);
						} else {
							cancelAnimation(animationFrame(step));
						}
					};
					animationFrame(step);
				}
				const animationFrame = window.requestAnimationFrame;
				const cancelAnimation = window.cancelAnimationFrame;
				const a = document.getElementsByClassName('school_count')[0]?.value || 14000;
				const text1 = document.getElementById('0101');
				const text2 = document.getElementById('0102');
				const text3 = document.getElementById('0103');
				const load = () => {
					animate(text1, 0, 14000, 7000);
					animate(text2, 0, 16500, 7000);
					animate(text3, 0, 12000, 7000);
				}
			</script>
